operation.search:
// Set the login_url if it's not already set (but careful not to send us to an attachment).
	if (empty($_SESSION['login_url']) && isset($_SESSION['old_url']) && strpos($_SESSION['old_url'], 'dlattach') === false &&  preg_match('~(board|topic)[=,]~', $_SESSION['old_url']) != 0)
		$_SESSION['login_url'] = $_SESSION['old_url'];

operation.add:
// PJL Redirect on login/logout Mod.
	if (!empty($modSettings['logon_redirect_enable']) && !empty($modSettings['logon_redirect_url']))
	{
		if (empty($_SESSION['login_url']))
			$_SESSION['login_url'] = $modSettings['logon_redirect_url'];
	}
// End Mod.

